import "./App.css";
import Alert from "./components/Alert";
// import About from "./components/About";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import React, {useState} from 'react';  

function App() {
  const [mode, setMode] = useState('light');
  const [gmode, setGmode] = useState('light');
  const [alert, setAlert] = useState(null);


  const showAlert = (message , type) =>{
    setAlert({
      msg : message,
      type : type
    })
    setTimeout(() => {
        setAlert(null);
    }, 1500);
  }

  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark')
      document.body.style.backgroundColor = '#1a2e41'
      showAlert("Dark Mode is Enable","success");
    }
    else{
      setMode('light')
      document.body.style.backgroundColor = '#aebed5'
      showAlert("Light Mode is Enable","success");


    }
  const greenMode = ()=>{
    if(mode === 'light'){
      setGmode('dark')
      document.body.style.backgroundColor = '#1a2e41'
      showAlert("Green Mode is Enable","success");
    }
    else{
      setGmode('light')
      document.body.style.backgroundColor = '#aebed5'
      showAlert("Light Mode is Enable","success");


    }
  }
  return (
    <>
      <Navbar title="TextUtil" mode={mode} toggleMode={toggleMode} greenMode={greenMode}/>
      <Alert alert={alert}/>
      <div className="container my-3">
        <TextForm heading="Enter the text to analyze below"mode={mode} showAlert={showAlert}/>
        {/* <About/> about="About Us"  */}
      </div>

    </>
  );
}
}
export default App;
